DROP TABLE IF EXISTS `danmu`;
CREATE TABLE `danmu`  (
  `id` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '弹幕的唯一标识（UUID）',
  `user_id` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '用户ID，外键连接到users表的id，表示发送弹幕的用户',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '弹幕的内容',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '弹幕发送的时间',
