-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `id` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '唯一标识，使用 UUID',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '公告标题',
  `date` date NOT NULL COMMENT '发布日期，格式为 YYYY-MM-DD',
  `publisher` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '发布者名称',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '公告内容，存储 HTML 格式文本',
  `cover_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '封面图片的URL地址',
  `type` tinyint(4) NOT NULL COMMENT '类型：1-通知公告，2-媒体报道，3-慈善人物',
  `is_recommended` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否推荐：0-未推荐，1-推荐',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '新闻公告表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES ('111ba976-4eee-4965-a209-973763e260fe', '情系革命老区 善暖困难群众 —— 心阳慈善总会“慈善情暖万家”走进湖北黄冈', '2024-12-17', '本站', '<p class=\"ql-align-justify\">冬意渐浓，荆楚大地已是寒风掠境，霜染枝头，但是中华慈善总会的到来，却为这里带来阵阵暖意。12月13日，中华慈善总会“慈善情暖万家”捐赠慰问活动走进湖北省黄冈市。中华慈善总会党委第一书记、会长宫蒲光率队送爱输暖。</p><p class=\"ql-align-justify\"> “慈善情暖万家”是由中华慈善总会牵头，发动全国各地慈善会在每年元旦春节前后共同实施的一个大型慈善助困活动和品牌项目，已连续开展了21年。此次中华慈善总会捐赠100万元善款，携手波司登公益基金会捐赠1000件波司登羽绒服，与湖北省、市、县慈善总会共同开展“慈善情暖万家”活动，深入基层一线走访慰问，有针对性地帮助困难群众解决问题。</p><p class=\"ql-align-justify\"> 集中捐赠仪式在黄冈市红安县举行。湖北省慈善总会会长陈天会、驻会副会长兼秘书长彭文洁，黄冈市慈善总会会长李儒志，市民政局党组书记、局长、市慈善总会常务副会长王风华，市慈善总会副会长兼秘书长邹德元等参加仪式。红安县政协副主席刘美华主持仪式。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20241217/856a268469af4442adb889e8ced541e4.jpg\" alt=\"新闻封面\">图为在黄冈市红安县举行的集中捐赠仪式现场</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\"> 宫蒲光代表中华慈善总会向热情支持本次活动的湖北省各级党委政府、慈善组织、爱心企业及爱心人士表示衷心感谢，并向红安县父老乡亲致以真挚的问候。宫蒲光表示，认真贯彻落实党中央、国务院和民政部要求，与全国各地慈善会一道形成合力，助力乡村振兴，既是慈善组织义不容辞的使命担当，也是爱心企业履行社会责任的重要体现。黄冈是我党早期革命活动的重点区域，也是鄂豫皖革命根据地核心区域。红安县是全国最著名的将军县，在这里打响了黄麻起义第一枪，诞生了红四方面军、红二十五军、红二十八军三支红军主力，牺牲了无数英雄儿女，为中国革命作出重大的贡献，是革命老区的典型代表。让慈善的暖阳照亮这里的每一个角落，是慈善组织应尽的职责。</p><p class=\"ql-align-justify\"> 宫蒲光对波司登集团卓越的社会责任担当予以高度称赞，他说，波司登集团始终以履行社会责任为企业的核心价值追求，深耕公益慈善领域，释放了强大的社会正能量。7年前，波司登集团与中华慈善总会签署了长达10年的捐赠协议，捐赠价值2亿元的羽绒服，用于每年冬季向各地困难群众送温暖。7年来，这项活动覆盖了全国大部分省(自治区、直辖市)，近14万困难群众获得帮助，收到了良好的社会效果。</p><p class=\"ql-align-justify\"> 红安县人大常委会党组书记、主任张治礼表示，正值民政系统和慈善公益组织认真学习贯彻习近平总书记对民政工作的重要指示和全国第十五次民政会议精神之际，中华慈善总会带着对老区人民的深情厚谊，带着对红安慈善事业的殷切希望，看望慰问红安困难群众，这是心系基层，察民情、解民忧、暖民心的重要举措。红安将以此次活动为契机，持续把慈善事业作为推进红安社会进步发展的一大抓手，持之以恒传播慈善文化、实施惠民项目、搭建慈善桥梁，多办惠民之事、多行务实之举、多发慈爱之声，把慈善救助工作做到群众心坎上，绘就有温度、有力量、有情怀的红安慈善公益画卷。</p><p class=\"ql-align-justify\"> 在庄重而温情的仪式现场，红安县30名困难受助对象逐一接收了慰问金和慰问物资。受助对象秦基喜激动地说：“有幸接受中华慈善会的资助，正如冬日遇上暖阳，久旱逢上甘露，让我感激万分。你们的资助，让我对生活充满了希望。此时此刻，世界上最美丽的语言也都无法表达我的心情。”</p><p class=\"ql-align-justify\"> 14日，宫蒲光一行来到麻城市黄土岗镇，入户慰问困难家庭。宫蒲光一行首先来到小漆园村监测户成学术家。成学术59岁，患慢性肾脏病4期、高血压病2级，完全没有劳动能力。他的儿子36岁，已患尿毒症12年，靠一周3次的透析维持生命。全家收入靠成学术的妻子在黄土岗镇福利院打零工以及儿媳在外务工，家里还有11岁的孙女在上学。在成学术家，当宫蒲光得知当地有关部门提供了不少帮助时，感到非常欣慰。他详细询问成学术在生活中还有什么困难与需求，并鼓励一家人积极乐观面对困难、战胜困难。</p><p class=\"ql-align-justify\">&nbsp;</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\"> 随后，宫蒲光一行又来到堰头垸村村民黄吉涛家。黄吉涛一家4人共同生活。其儿媳2021年1月被查出尿毒症，每周透析两到三次;孙子为初中一年级学生，今年8月被查出尿酸高，只能休学在家养病。全家收入靠黄吉涛儿子一人务工。宫蒲光给黄吉涛家送上慰问金以及慰问物资，与他们进行心贴心交谈，详细询问他们的生活情况和身体状况，了解生活中存在的困难和问题，勉励他们相信党和政府，乐观面对生活。</p><p class=\"ql-align-justify\"> 15日，宫蒲光一行又来到黄梅县黄梅镇，未作丝毫停歇即投身入户走访慰问。慰问对象方芳堪称当地的励志典型。3岁那年，一场高烧无情地夺走了方芳的听力，从此她的世界陷入了寂静。但她并未向命运低头，4岁时毅然决然地开启了自学游泳之路。2023年湖北省残运会上，方芳凭借着出色的发挥和顽强的毅力，成功斩获S15级仰泳50米冠军金牌。方芳的故事，如同一盏明灯，激励着残疾人勇敢地追寻自己的梦想。在方芳所在的黄梅特殊学校，宫蒲光详细询问她的身体、生活、家庭收入情况以及生活需求，鼓励她发挥自身的优势，给他人更多的精神动力，力所能及地服务社会。方芳表示将更加努力拼搏，向着更高的目标前进。</p><p class=\"ql-align-justify\">&nbsp;</p><p class=\"ql-align-center\">图为宫蒲光一行在黄梅特殊学校慰问，当听到方芳和刘苗两位同学身残志坚、自强不息的事迹后，向两位伸出大拇指，并勉励她们更加努力拼搏，在人生路上闪耀出属于自己的光芒</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\"> 同样身处黄梅特殊学校，沐浴在慰问暖光中的刘苗，亦是一位令人钦佩的自强不息典范。刘苗的家境贫寒，雪上加霜的是，刘苗出生不久便被诊断为听力缺失。7岁起，刘苗踏上特殊学校学习之路。初中毕业后，选择学习西点烘焙的她，心中燃着一团炽热火焰——早日学成就业，挑起家庭重担。刘苗付出了比常人更多的努力，逐渐掌握了这门手艺。后来，刘苗进入三叶草蛋糕店学习蛋糕制作。她很快习得娴熟的手法，用自己的双手为顾客送上甜蜜，也为自己家庭带来希望。宫蒲光对刘苗的顽强生活斗志和乐观的生活态度表示赞赏，关心地询问刘苗现在的就业和生活以及家庭情况，勉励她用自己特有的生活热情创造美好的未来。</p><p class=\"ql-align-justify\"> 紧接着，宫蒲光一行又走访慰问了为救落水儿童而牺牲的英雄涂晓芸家庭。涂晓芸2001年为救落水儿童而牺牲，被评为烈士。涂晓芸的父亲涂再汶一直在村干部的岗位上工作，退休后长期参加志愿服务。涂晓芸的英雄事迹和涂再汶的公益热情，让宫蒲光深受感动。他询问涂再汶的退休生活状态，了解他有没有什么困难，祝福他晚年生活更加精彩，并叮嘱当地有关部门进一步主动服务，切实帮助困难家庭解决“急难愁盼”问题。</p><p class=\"ql-align-justify\"> 受访的群众都深受感动，表示他们将保持乐观的心态，坚定克服困难的信心，通过自己的不断努力，把生活过好，不辜负社会各界的关心关爱。</p><p class=\"ql-align-justify\"> 短短3天，慰问组一行在为黄冈困难群众送去来自社会各界慈善温暖的同时，也切身感受到了革命老区所蕴含的强大精神力量，这是一场爱心和感谢的双向奔赴。</p><p class=\"ql-align-justify\"> 湖北省慈善总会驻会副会长郭志高，黄冈市委常委、宣传部部长李初敏，黄冈市副市长肖红娟，黄冈市慈善总会会长李儒志，麻城市市长裴永波、副市长程伟，麻城市慈善总会会长郑立新，黄梅县委常委、统战部部长罗盘军，县政府副县长熊仲钢，县慈善会会长李仲良，中华慈善总会副秘书长兼宣传部部长曾春光、办公厅副主任兼筹募部副部长于洪悦、项目部副部长梁宏业、新闻界志愿者慈善促进工作委员会副秘书长盛春岚等分别参加走访慰问活动。（章昱兴）</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/856a268469af4442adb889e8ced541e4.jpg', 2, 1);
INSERT INTO `news` VALUES ('153c6918-2319-4bdf-ab85-a1c1489f23e6', '慈善刘维隆', '2024-12-18', '本站', '<p class=\"ql-align-center\"><span style=\"text-align: left; background-color: var(--el-drawer-bg-color);\">又走近陕西慈善，又见到会长刘维隆，又感到一股迎面吹来的暖风，又被火一样的激情激励，又被春一样鲜活、海一样深邃的思维启迪。</span></p><p>	他迎上来抱住我，高声说：“我们是老朋友。来，拥抱一下！”</p><p>	……</p><p>	刘维隆，一个标准、典型的西北汉子，朴讷温厚而又率直豪放。他个子不矮，魁梧身材，矫健步态。一双和善、睿智的明亮眼睛上边，长着两道传神的浓眉。眉宇间漾着英气更洋溢着长久的宽厚和慈祥。他富态的国字形脸庞上总浮有浸着阳光的微笑。他的微笑真诚、纯净而又自信、豁达。让人感到亲切、平等，使旁人对他瞬间就没了陌生感、距离感。</p><p>	这时，2016年4月2日，一个周六的傍晚。</p><p>	我这次来，是应邀参加“陕西省慈善教育工作会议”。这无疑又是一次能给人以震撼的会议。他们将在会上部署全省的慈善教育工作，决定将“慈善文化进校园”覆盖到全省的中小学。听到这个信息时，我心里不觉一惊。这无疑是慈善事业发展进程中的大手笔、大举措。做到这一点实在不是一件容易的事情，不知他们为此已经付出、还要付出多少智慧和艰辛。</p><p class=\"ql-align-center\"><img src=\"http://file.chinacharityfederation.org/Image/20160426/20160426104715_1502.jpg\"></p><p>&nbsp;</p><p>	想想2011年底，中华慈善总会刚刚启动“慈善文化进校园”项目，刚刚和上海新纪元教育集团合作开发出我国第一套系统的中小学慈善类读物《慈善读本》的时候，社会上的各种议论一时间风起云涌、铺天盖地。虽然绝大多数都是赞扬、支持的，但质疑、否定的声音也不甘示弱。由于有这种质疑的存在，由于之前还没有哪个组织有过这样的尝试，所以实施这一项目真不是一件轻松、容易的事情。就在这艰难的开创时刻，刘维隆和陕西省慈善协会的同志们决定配合中华慈善总会在全省一千所中小学开展以《慈善读本》捐赠为载体的慈善文化进校园活动。他们开会推动这一项目时，我陪中华慈善总会当时的会长范宝俊到西安出席会议。那时，我第一次见到会长刘维隆。我没有想到，这位出任陕西省慈善协会会长时间并不长的原省人大常委会副主任，对“慈善文化进校园”的认识和理解，竟然比我们还要深刻、还要到位。他爽朗地笑着，一字一板地说：“总会这个决策意义重大啊！这是我们慈善组织的基因工程、种子项目。我们一定尽全力把这个项目搞好！” 谈到《慈善读本》时，他说：“《慈善读本》内容生动活泼，通过循循善诱讲故事的方式进行慈善基础教育，是本次慈善教育项目的载体。”我注意到，范宝俊会长听到刘维隆会长这样讲时，眼睛顿时一亮。那以后好长一段时间里，范宝俊会长常常会和我谈起陕西省慈善协会会长刘维隆，说这个会长对慈善工作有个钻研劲儿，有思路、有作为，为人很好。还说，刘维隆重感情、对老同志很尊重。这我也看到、感觉到了。每次到陕西参加慈善的重要活动，我都会看到刘维隆会长热情邀请老会长徐山林参加，并主动真诚地听取老领导的意见、建议。那次会议，徐山林不但参加会议，还讲了一段很深刻的话。他说：“慈善教育进校园是对青少年慈善意识及慈善素质的培养与提升，并树立青少年的仁爱之心、社会担当及关爱他人的精神。学校应将‘学’与‘做’紧密结合，引导学生参与公共事务与志愿活动，使慈善教育的概念得到扩展。”我们都知道，陕西省慈善协会自创办起，就十分重视慈善文化、慈善教育的开创与传播。全国第一部《慈善诗选》、第一部《慈善报告文学》都出自陕西。刘维隆任会长之后，不但继承了这一好的传统，而且越做越大、越做越强，发扬得多姿多彩。</p><p>	为实施“慈善文化进校园”这个全新的慈善项目，刘维隆带领慈善会一班人积极向省教育部门的领导层推荐、宣传，并直接到省财政厅协调。在组织好社会募捐的同时，争取到财政支持的1000万元。向全省千所中小学捐赠《慈善读本》百余万册。参加大会那天，我特别注意到，省教育厅副厅长讲话时，几乎没看一眼讲稿。讲“慈善文化进校园”、讲《慈善读本》，讲得头头是道。我有点吃惊，但我马上就想到了陕西省慈善协会的工作，想到了会长刘维隆。我曾听陕西省慈善协会副会长张晓菊说，联系教育厅的工作是很麻烦、很艰难、很费周折的。人家对“慈善文化”并没有太多的了解，以往与慈善协会的联系也不很多。再说也没有教育部的明确指示。要不是刘维隆会长亲自出马一次次地宣传鼓动，这件事的成功几乎是不可能的。我想，省教育厅的领导们显然是被刘维隆会长深邃的思维、生动的讲述打动，并对慈善文化、慈善教育做了一番实际的研究与探讨，心悦诚服地认同刘维隆会长的宣传鼓动。这次到西安参加“陕西省慈善教育工作会议”，出席这个会议并讲话的是陕西省教育厅厅长王建利。正厅长的工作是很忙的，这一段时间每个工作日都排满了任务。所以，这个会议原本安排一位副厅长出席。但刘维隆会长坚持请正厅长出席。但厅长只有星期天有空，刘维隆会长就和厅长商量，拍板星期天开会。那天，王建利厅长很高兴地出席会议。讲话时精神饱满还有点激动，他特别强调“全省各级教育行政部门、各中小学长期有责任把慈善教育作为立德树人的重要任务抓实抓好。”指出：“省教育厅将继续指导和要求全省各级教育行政部门，积极协同各级慈善协会，建立健全分工明确、齐抓共管的工作机制，完善慈善教育联席会议制度，及时研究和分析慈善教育推进过程中出现的新情况、新问题，全力推进慈善教育活动在更多中小学常态化开展。各级教育部门也要加强与同级慈善机构的协作与配合，建立起协调共商机制，心往一处想，劲往一处使，共同努力把我省的慈善教育活动打造成中小学德育的品牌和亮点。”</p><p>	那天的会议，全省各地的教育局长都到会了。</p><p>	张晓菊副会长对我说，这次会议之后，各地教育部门对实施“慈善文化进校园”项目的积极性甚至超过了我们各地的慈善会。</p><p>	“慈善”的影响力在陕西可见一斑。</p><p>	刘维隆任会长之后，在慈善文化、慈善教育方面陕西当然不止在这一方面有力度有建树。据我所知，他们还率先在全国搞了“慈善文化进大学活动”；成功组织了“慈善书法大赛”“爱心歌曲大家唱”和“慈善文学创作评选”等颇具影响力的活动。在加强慈善理论研究方面，陕西省也有许多令人瞩目的成果。当然也不止这些，在慈善募捐、慈善救助方面，陕西的创新步伐和成果更是令人惊喜。比如，他们“创新方式，形成慈善募捐多元新格局。”即使是在经济状况困难的情况下，慈善的募捐量也连年大幅度提升；他们“加强管理，大力提升慈善项目规范化运作水平”。一手抓原有的慈善品牌项目的实施，一手抓新的慈善项目的创立发展，并使之成为新的品牌项目；他们还“发展慈善实体，壮大慈善事业”并摸索了经验、取得了成效。</p><p>	刘维隆任省慈善会会长之后，就给慈善会明确了一个很贴切、也很明确和生动的定位。在四处奔波的慈善工作中，刘维隆常常会讲到这个定位，常讲常新。最近，在一个地区的慈善工作中，他就又一次强调：“省慈善协会是慈行善举的平台，是爱心人士的家园，慈善事业是改善民生的重要补充，是打赢扶贫攻坚战不可或缺的重要力量。省慈善协会是慈善人的家，应为爱心人士服好务，为困难人群送温暖，敢于担当、乐于奉献，为建设美丽陕西、和谐陕西、富裕陕西做出贡献！”</p><p>	刘维隆讲“慈善”时，很少看讲稿。他的讲稿或者由他自己起草，或者由他执笔一遍遍修改。“慈善”已经完全融入了他的血液、他的生命、他的行动之中。他的老伴儿曾有点抱怨地说他晚上说梦话说的都是慈善。</p><p>	刘维隆在许多重要工作岗位做出过突出贡献，并成长为一名省部级领导干部。谈及自己的仕途生涯，他说“就我个人当初主观而言，确属被动和偶然。”刘维隆是“文革”前的“老三届”，1968年高中毕业后下乡，在汉中一个小村庄插队。1971年，他被选调到陕西省汉中地区钢铁厂。他先是当了一名炉前工。由于诚实肯干能干，他先后被工友和领导拥戴着当了班长、炉长、车间主任、副厂长、党委书记。1990年，上级组织决定调任他到汉中市西乡县当县长。当时，工人们都舍不得这个一直与大家一起摸爬滚打的领导离开。那之后，他又先后担任县委书记，商洛地区行署专员、商洛地委副书记、省环境保护局党组书记、局长，省财政厅党组书记、厅长兼省地税局党组书记，陕西省第十一届人大常委会副主任。</p><p>	谈及自己的成长，他曾说：“检视20年来的从政心路，尚可自慰的是我一直生活工作在事业的意义和进取之中。以人为念，以事为本，以情为重，从未将一己的私心私利放置在先，而是让自己的精神与魂魄激荡在经世济民的家国情怀之中。它使我的经历艰难而又美丽、充实而显幸运。历史之命有嘱托，大地之子重耕耘。每至一处一地，是组织、群众和同舟共济的同事们为我搭建舞台，给我信心力量，教我做人做事，他们所给予我的远比我所能奉献的要多要深要广。”</p><p>	无论过去在从政的道路上，还是在慈善组织中当领导者组织者，刘维隆都“常抱一颗不懈学习、耽于研读、执着事业之心，历艰险而不畏，有高峰必攀登。”都始终秉承“敬畏历史、紧贴大地、躬身为民”的宗旨。</p><p>	刘维隆的慈心善举，慈善作为是一脉相承的。刘维隆的慈善有时代特点、有进取创新精神。</p><p>	这几年里，我逐渐看到、感觉到、体会到刘维隆的慈善思想、刘维隆的慈善行动、刘维隆的慈善信念、刘维隆的慈善情感。”</p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/20160426104815_9328.jpg', 3, 0);
INSERT INTO `news` VALUES ('17ab4927-d91f-43c1-8538-9772b0dacb1a', '牙缝里抠出生活费 耄耋夫妻10年助学180人', '2024-12-28', '本站', '<p class=\"ql-align-center\"><img src=\"http://file.chinacharityfederation.org/Image/20140922/20140922103456_8413.jpg\"></p><p class=\"ql-align-center\">10年来，郭长沙、洪淑惠夫妇坚持捐资助学。</p><p><br></p><p>10年前，晋江金井镇钞岱村小学举行校庆，正好逢着当地村民郭长沙和洪淑惠的50年金婚庆典。老夫妻一商量，便开始了捐资助学的公益之路。如今，再过一个月，他们就结婚满60年了，洪淑惠算了算，这10年来，他们资助了180多名学生，“我们老人家不懂得花钱，就捐出去做点有意义的事，挺好的。”</p><p><br></p><p>弥补遗憾 热心捐资助学</p><p><br></p><p>昨日上午，记者跟随钞岱村村主任郭少璞前往郭长沙夫妇的家。村道旁，有一栋两层的石头房，从旧式楼梯走到二楼，一间简陋的房间里，86岁的郭长沙和80岁的洪淑惠正坐着聊天。</p><p><br></p><p>谈及公益，洪淑惠告诉记者，2004年，金婚的时候，老伴说要把积蓄拿出来，帮助村里那些因为穷苦上不了学的孩子。“我们有四个子女，都没能顺利完成学业，所以希望帮助那些孩子顺利完成学业，弥补心里的遗憾。”为着这个朴素的心愿，老夫妻同心携手，家中墙壁上的老照片、柜子里的纪念牌，都是他们一路捐资助学做公益的见证。</p><p><br></p><p>省吃俭用 每月攒下退休金</p><p><br></p><p>帮助别人，很慷慨，自己生活中，却非常节俭。郭长沙烟酒不沾，连茶也不喝，除了日常三餐外，每月开支就一项——理发。说到理发，洪淑惠满是“责备”：“他啊，去理发，听到理发师生活困难，原本只需付10元，他硬是给了人家15元。”而在一日三餐上，老夫妻喜食清淡，地瓜稀饭是最爱，硬是从牙缝里再“抠”出些钱攒下来。</p><p><br></p><p>就这样，郭长沙将每月2000多元的退休金及子女们给的生活费，全攒下来了。洪淑惠说：“他傻，攒着钱也不会花，倒不如捐出去，帮助需要的人。”相伴走过六十载人生路，洪淑惠虽然口气里满是“责备”，却总是默默支持着老伴的举动。如今，郭长沙的身体大不如前，听力严重下降。采访过程中，他一直安静地坐在一边。</p><p><br></p><p>助学10年帮扶180名学子</p><p><br></p><p>今年是他们结婚的第60个年头，钻石婚之际，再回忆从金婚那年开始的这条公益路，洪淑惠说，这10年来，他们陆陆续续用27万元，资助了180多名学子完成学业。“他们夫妻俩的助学之举确实让人佩服。我印象最深的是村里面的一个女孩，她是由重度残疾的父亲收养的。十几岁时，父亲去世了，她跟着婶婶生活，后来考上集美大学。老人家除了原本给她的资助，又另外拿出来3000元帮助她。现在这孩子已经毕业了，成了一名空姐。”钞岱村党支部书记郭建群向记者说道。</p><p><br></p><p>除了助学，街坊邻里的困难，两位老人同样看在眼里。邻居有个阿婆，两年内3个儿子相继去世，洪淑惠便时常串门陪伴她，帮她减轻白发人送黑发人的痛，并无微不至地照顾她的生活。“不管是谁，遇到这种事都会帮。”洪淑惠轻描淡写的一句话，满含深情。</p><p><br></p><p>郭长沙八十大寿时，还把儿女为他操办生日的钱捐出来，为村里铺了条“长寿路”。建祠堂、捐资老人会、奖励优秀教师、带头成立村慈善基金会……为村里做过多少事，他和老伴也记不清了，“哪里需要，我们多多少少都帮，钱不多，但都是心意。”</p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/20141224180730_1781.jpg', 3, 1);
INSERT INTO `news` VALUES ('1f848f8e-d945-4f0d-975c-1975c26ac95a', '爱心企业科曼医疗、北京耀致携手向中华慈善总会捐赠AED及训练机设备', '2025-01-06', '本站', '<p class=\"ql-align-justify\">慈善公益报（李希金 王成）2024年12月30日，爱心企业深圳市科曼医疗设备公司(以下简称科曼医疗)、北京耀致科技公司联合(以下简称北京耀致)向中华慈善总会捐赠AED(自动体外除颤器)仪式在北京举行。第十四届全国政协委员、中华慈善总会党委书记孙达，党委副书记、秘书长边志伟，总会大健康工作委员会总干事赵文华，科曼医疗设备公司董事长易勇，北京耀致科技公司董事长文家耀、总经理刘凯，广东美赛尔细胞生物科技公司总经理宋鸿云等出席活动。捐赠仪式由边志伟主持。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20250107/5efdf2f5fa874a4b82ba87eab35a1d4e.jpg\" alt=\"新闻封面\"></p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\"> 孙达代表中华慈善总会向科曼医疗、北京耀致和美赛尔生物科技等爱心企业的慷慨善举表示衷心感谢。他说：“以习近平同志为核心的党中央高度重视人民群众生命安全和身体健康， 将‘健康中国\'提升为国家战略。在这一大背景下，国家医药卫生事业正从‘以治病为中心’向着‘以健康为中心’转变，推进全面健康关口前移成为医药卫生事业的重大命题。”</p><p class=\"ql-align-justify\"> 作为第十四届全国政协委员，孙达在2023年全国两会上提交了《关于推进AED的大面积普及与急救联动的提案》，又在2024年全国两会上提交《关于提升全民应急救护能力与设立“中国急救宣传日”的提案》。两项提案均涉及推进中国AED普及与全社会急救事业发展，引起政府各部门与社会各界的广泛关注与积极回应。中华慈善总会一直都在积极与相关职能部门、医疗机构以及爱心企业、爱心人士进行联动，共同推动AED设备与急救技能普及工作。一年多来，南到云南普洱边境地区，西到新疆喀什乡镇医院，北到吉林白城街道社区，东到江苏常熟中小学校，中华慈善总会已向社会各界捐赠近700套AED设备，开展了多场次线下急救技能培训，慈善足迹遍布大江南北，为守护民众健康作出了积极贡献。</p><p class=\"ql-align-justify\"> 孙达指出，慈善事业与健康事业都是长期而艰巨的任务。推动AED的普及和急救技能的提升需要全社会的共同努力和持续投入。他希望能有更多爱心企业加入慈善事业中来，共同为构建更加健康、安全的生活环境贡献力量。</p><p class=\"ql-align-justify\"> 易勇在致辞中表示，作为一家富有社会责任感的企业，科曼医疗始终将公益事业视为己任，积极开展各类慈善公益行动。未来，科曼医疗将通过持续的捐赠活动和专业培训，助力AED的广泛普及与全民急救技能的提升，不断完善与巩固院前急救体系建设，为人民群众生命健康安全筑起一道坚实防线。</p><p class=\"ql-align-justify\"> 据了解，科曼医疗自2022年3月上市以来，凭借AED产品卓越的性能和质量，迅速赢得了市场认可，市场占有率已跻身全国前列，成功救治众多心搏骤停患者。同时，科曼医疗还积极参与多个大中城市AED安装布设项目，涵盖城市建设、交通、司法、教育、电力、旅游、体育等多个领域，为城市急救体系建设作出贡献。</p><p class=\"ql-align-justify\"> 刘凯在致辞中表示：北京耀致作为一家专门致力于AED普及推广与服务的企业，始终坚持“人民至上，生命至上”价值理念，积极履行社会责任。特别是在中华慈善总会号召下，已向社会多个领域捐赠AED设备，并开展应急培训活动。此次定向捐赠给多个省市公安基层所队的AED与AED训练机设备，同样具有特殊意义。公安队伍是国家政治安全、社会和谐稳定、人民幸福生活的保护者与捍卫者，是和平时期牺牲最多、奉献最大的钢铁队伍。能为更多民警辅警以及人民群众生命健康服务，是北京耀致义不容辞的使命与责任。</p><p class=\"ql-align-justify\"> 活动现场举行了捐赠授牌与证书颁发仪式。&nbsp;</p><p class=\"ql-align-justify\"> 此次科曼医疗、北京耀致共向中华慈善总会捐赠300台AED设备及1560台AED训练机。</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/5efdf2f5fa874a4b82ba87eab35a1d4e.jpg', 1, 1);
INSERT INTO `news` VALUES ('3d15b9bd-0cd0-4eef-901d-88b2822d7efc', '情系边疆群众 共写温暖篇章 —— 心阳慈善总会“慈善情暖万家”走进内蒙古', '2024-12-26', '本站', '<p class=\"ql-align-justify\">寒冬送温暖，关怀显真情。12月19日，一场温馨的捐赠仪式在内蒙古自治区呼伦贝尔市鄂温克族自治旗鄂温克族中学举行，中华慈善总会“慈善情暖万家”活动走进内蒙古。第十四届全国政协委员、中华慈善总会党委书记孙达率领慰问组，联合内蒙古自治区慈善总会为当地困难群众送去温暖和希望。&nbsp;</p><p class=\"ql-align-justify\"> 本次活动中，中华慈善总会向内蒙古自治区捐赠100万元善款，其中，向呼伦贝尔市境内鄂温克族困难学生捐赠16万元，向巴彦淖尔市乌拉特中旗、乌拉特后旗因旱灾需要帮扶的困难户捐赠10万元，向锡林郭勒盟境内因旱灾需要帮扶的困难户捐赠10万元，向通辽市科尔沁左翼后旗境内困难学生和特殊教育学校207名在校困难学生、特殊教育学校残疾学生捐赠24万元，向通辽市奈曼旗境内困难户捐赠10万元，向赤峰市宁城县境内困难户捐赠10万元，向赤峰市林西县境内孤儿、事实无人抚养儿童和困难户救助和帮扶10万元，向赤峰市松山区境内孤儿、事实无人抚养儿童和困难户救助和帮扶10万元。另外，中华慈善总会携手波司登集团向通辽市奈曼旗境内孤儿、事实无人抚养儿童和困难户捐赠羽绒服200件。</p><p class=\"ql-align-justify\"> 第十四届全国政协委员、中华慈善总会党委书记孙达在捐赠仪式上表示，党的十八大以来，以习近平同志为核心的党中央为推动慈善事业高质量发展，作出了一系列顶层制度设计和重大决策部署，指明了新时代慈善工作的前进方向。近年来，中华慈善总会与全国各地慈善会一道形成合力，联合开展慈善救助和帮扶行动，当好各级党委、政府的得力助手，这是慈善组织义不容辞的使命担当。孙达表示，学生们恰似初升的朝阳，承载着祖国的未来，凝聚着中华民族蓬勃发展的希望。希望公益慈善犹如冬日暖阳，激励孩子们更加努力学习，向上向善，做有志向、懂感恩，德智体美劳全面发展的新时代青少年，不辜负老师和家长的谆谆教导，不辜负党和人民的殷殷期望。</p><p class=\"ql-align-justify\"> 内蒙古自治区慈善总会副会长兼秘书长青格勒图表示，慈善助学是一项关注民生、关心教育、关爱学子的民心工程，也是一项奉献爱心、温暖民心、凝聚信心的真情善举。中华慈善总会联手内蒙古自治区慈善总会和内蒙古自治区鄂温克族研究会，帮助困难家庭学生铺开“筑梦之路”、点燃“希望之火”，不仅是经济上、物质上的帮助，更是精神上的极大鼓舞和鞭策。内蒙古自治区慈善总会将发动更多爱心企业和爱心人士参与公益慈善事业，做好教育帮扶这项造福社会的工作，把爱心善举奉献给需要关怀和帮助的困难学子。</p><p class=\"ql-align-justify\"> 鄂温克自治旗政府副旗长卓仁在欢迎词中表示，中华慈善总会携手自治区慈善总会和自治区鄂温克族研究会，对鄂温克族困难学生进行慈善捐赠，既是支持教育事业、关心边疆少数民族青少年健康成长的一项民心工程，又是奉献爱心、帮助牧区中小学生实现学业梦想的崇高义举，相信爱心的传递一定会成为托举鄂温克族青少年实现梦想的重要力量。</p><p class=\"ql-align-justify\"> 呼伦贝尔市政协原副主席、内蒙古自治区鄂温克族研究会顾问达喜扎布在致辞中表示，中华慈善总会“慈善情暖万家”项目联合内蒙古自治区慈善总会，为鄂温克族困难学生献爱心、送温暖，帮助他们实现学业梦想，增强了他们摆脱生活困境的信心，对推动鄂温克族教育事业的发展，为民族的发展培养更多优秀人才，具有重要意义。</p><p class=\"ql-align-justify\"> 仪式结束后，孙达一行入户走访了困难学生家庭。鄂温克旗第一实验小学最低生活保障家庭学生小娜同学，父亲在她出生9个月时因病去世，母亲也有残疾，无法从事重体力劳动。虽然爷爷奶奶照顾她，但由于他们年事已高，身体状况也不佳。2023年，爷爷重病，到现在仍需长期服药控制病情。孙达一行给小娜同学家送去了食用油、大米等生活物资及慰问金，鼓励他们积极面对困难，相信在党和政府的关怀下，在社会各界的帮助下，家庭的状况会逐步改善，生活会更加美好。小娜同学的家长无比感激，说中华慈善总会的帮助减轻了家庭的负担，同时表示他们会用心呵护和培养孩子，激励孩子更加努力学习。</p><p class=\"ql-align-justify\">&nbsp;</p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241225/b3f6413b3050937fed56fa535ad9482f.jpg\" height=\"487\" width=\"666\"></p><p class=\"ql-align-center\">孙达（右二）一行在鄂温克旗第一实验小学小娜同学家中慰问</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\"> 鄂温克族自治旗第二实验小学的小嘉同学是个先天唇腭裂患者，命运多舛的童年让她非常坚强，更加懂得爱与奉献。她学习勤奋刻苦、品学兼优。孙达一行与学生家长亲切交流。当孙达得知小嘉同学曾得到中华慈善总会的“微笑列车”爱心援助，从出生50天到7岁，“微笑列车”项目无偿为她做了三次手术时，感到非常欣慰。孙达对女孩的坚强、自信、乐观表示赞扬，并生动讲述了中华慈善总会的发展历程，分享了一些困难家庭的唇腭裂、先心病、包虫病患儿在中华慈善总会的帮助下，正视困难、顽强成长的故事。孙达鼓励小嘉同学在困境中坚守梦想，相信党和政府一定不会抛弃任何一个家庭，社会一定会越来越温暖。</p><p class=\"ql-align-justify\"> 孙达一行还调研了鄂温克族自治旗创业园，了解了鄂温克创业青年的故事，并给予了他们很大的肯定和鼓励。</p><p class=\"ql-align-justify\"> 中华慈善总会副秘书长兼宣传部部长曾春光，呼伦贝尔市人大常委会副主任景泽，鄂温克旗委副书记、旗长敖慧然，鄂温克旗副旗长卓仁，内蒙古自治区鄂温克族研究会副会长布仁巴雅尔、乌日娜、副会长兼秘书长乌云娜等参加活动。（章昱兴）</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/d69b375d8f654c73aa4fd18d19a4c820.jpg', 2, 0);
INSERT INTO `news` VALUES ('483f06e6-6f75-480a-99cd-5ddfd8def19e', '发展多元慈善 赋能红色沃土 —— 心阳慈善总会党委第一书记、会长宫蒲光在湖北调研纪实', '2024-12-24', '本站', '<p class=\"ql-align-justify\">浓浓慈善情，拳拳为民心。中华慈善总会“慈善情暖万家”慰问活动为各地困难群众送去温暖与关怀，彰显了社会大爱，体现了慈善组织的为民情怀和责任担当。正如中华慈善总会党委第一书记、会长宫蒲光所说，让慈善的暖阳照亮每一个角落，是慈善组织应尽的职责。</p><p class=\"ql-align-justify\">寒冬时节，宫蒲光率队在湖北开展“慈善情暖万家”活动时，不但直接对困难群众送去帮助和关爱，更关心当地慈善事业的发展，关心慈善事业对助力乡村振兴和共同富裕的作用，走访慰问的同时，对当地慈善工作情况进行了深入的考察调研。</p><p class=\"ql-align-justify\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20241227/078e8553c0134e8893acced9a905bf50.jpg\" alt=\"新闻封面\"></p><p class=\"ql-align-center\"><strong>湖北样本和湖北经验值得推广</strong></p><p class=\"ql-align-justify\">12月12日，宫蒲光一行到达武汉。在湖北省慈善总会，宫蒲光一行先后参观了抗疫展览室、档案室、办公区等，详细了解慈善筹募、项目实施及内部管理等工作情况。在随后举行的座谈会上，湖北省慈善总会就机构治理、慈善项目、慈善募捐、应急慈善等相关情况作了工作汇报。&nbsp;</p><p class=\"ql-align-justify\">据介绍，近年来，面对新冠病毒感染疫情后经济尚未全面恢复，慈善资源相对短缺等不利环境，湖北省慈善总会全体人员克难奋进，笃行实干，主要任务目标逆势上扬，年度募款总量屡创历史新高。自本届理事会换届以来，共动员超720万人次参与捐赠，募集款物价值超159亿元。</p><p class=\"ql-align-justify\">慈善实践中，湖北省慈善总会重视创新发展，将济困型与发展型慈善项目有机结合起来，联动全省市县两级慈善组织，陆续实施助力乡村振兴的“幸福家园”村社互助项目，坚持以医疗救助和人文关怀为重点开展“医路同行”慈善医疗项目，稳步开展困境儿童救助、退役军人帮扶等工作，慈善项目聚合效应日益凸显。</p><p class=\"ql-align-justify\">为适应信息社会的发展新趋势，湖北省慈善总会将慈善信息化建设作为重要发展战略，通过多年持续不断的投入、研发和创新，在全国率先建立起了捐赠、救助、管理一体化的慈善信息平台，每年有超百万人次通过互联网参与慈善。</p><p class=\"ql-align-justify\">湖北省慈善总会重视全省慈善联动，经过深入调研，提出市县两级慈善组织机构建设及人员配备等标准，得到了地方党委政府的高度重视，形成了全省市(州)、县(市、区)慈善会上下联动的工作格局，不断增强全省慈善体系的社会影响力。</p><p class=\"ql-align-justify\">湖北省慈善总会会长陈天会表示，中华慈善总会慰问组冒着严寒到湖北调研指导工作，为湖北困难群众送来慰问资金和物资，充分体现了中华慈善总会对湖北慈善工作的高度重视和对荆楚大地困难群众的深切关怀。慈善组织实现高质量发展，必须依照慈善法，依法治善，依法行善，照章办事，严格管理。慈善募捐来的款物都具有来源渠道的特殊性、使用性质的公益性、社会高度关注的敏感性，一次大的差错，就会使多年积累起来的社会公信力瞬间荡然无存。对此，慈善从业者一定要保持高度的清醒与自觉，要有如履薄冰、诚惶诚恐的履职心态，管好用好每一分资金、每一件物资。湖北省慈善总会将积极探索创新，跟上时代的步伐，踏准时代的节奏，适应社会的变化和需求，脚踏实地，久久为功，确保慈善事业行稳致远。</p><p class=\"ql-align-justify\">宫蒲光对湖北省在中国特色慈善事业发展道路上所进行的有益探索、取得的成绩、获得的经验表示充分肯定。他说，湖北省慈善总会作为全省枢纽型慈善组织，深刻领会党的二十大和二十届三中全会精神，准确把握新时代慈善事业高质量发展的正确方向和实践要求，以问题为导向，从实际出发，推动了湖北慈善事业的良好发展，为中国慈善事业高质量发展提供了湖北样本、贡献了湖北经验。湖北样本和湖北经验值得推广。</p><p class=\"ql-align-justify\">宫蒲光表示，新时代有新要求，推动慈善事业更好更快发展，要进一步创新募集慈善款物的方式、方法和手段，充分挖掘慈善资源;要着眼群众关切的“急难愁盼”问题，有针对性开展项目，解决实际问题;要弘扬慈善文化，推进全民慈善，营造全社会参与慈善、支持慈善的良好氛围;要加大慈善专业化建设的力度，组建专业化队伍。中华慈善总会将和各地慈善会加强互动，强化协作，互相借鉴，共同进步，不断推动慈善事业迈上新的发展台阶。</p><p class=\"ql-align-center\"><strong>让每一笔捐助都用到最需要的地方</strong></p><p class=\"ql-align-justify\">12月13日，宫蒲光一行在黄冈市红安县参加“慈善情暖万家”集中捐赠仪式和入户慰问之余，全面了解了黄冈市和红安县慈善事业发展情况。黄冈市慈善总会会长李儒志、红安县慈善会常务副会长吴祖焱分别介绍情况。</p><p class=\"ql-align-justify\">黄冈作为中共早期建党活动的重要驻地和鄂豫皖革命根据地的中心，在多年的革命斗争中，铸就了“紧跟党走、不屈不挠、艰苦奋斗、无私奉献”的老区精神。多年来，黄冈市慈善总会秉承老区精神，积极募集慈善款物，近5年来，黄冈市慈善总会指导全市慈善机构募集慈善资金和物资价值总额达29.58亿元。其中市本级捐赠款物价值14.32亿元。利用慈善款物，各级慈善会助力疫情防控，助力抗洪救灾、脱贫攻坚、乡村振兴，救助特困特优学生、大病贫困患者、城乡困境儿童，惠及全市困难群众近百万人，在社会民生保障方面发挥了积极作用。</p><p class=\"ql-align-justify\">红安县慈善会自2014年成立以来，通过开展形式多样、渠道丰富的慈善活动，致力于推动共同富裕，助力乡村振兴，保障和改善民生，凸显了慈善在第三次分配中的关键作用。自2020年以来，连续荣获湖北省慈善总会、黄冈市慈善总会先进会员单位，获评慈善联动项目实施先进单位及湖北数字公益节项目筹款先进单位，2022年被评为黄冈市4A(AAAA)级社会组织，2024年 9月荣获“第二届中华慈善数字公益节”共建幸福家园活动启星单位。</p><p class=\"ql-align-justify\">宫蒲光对黄冈市慈善总会和红安县慈善会取得的优异成绩表示祝贺。他说，两级慈善会将保障民生作为工作的重中之重，实施了一系列助力乡村振兴、助医、助老、助残、助孤、助学、助困等项目，打造了一批叫得响、可持续的慈善品牌，为市、县助力疫情防控、脱贫攻坚、抗灾救灾、助学助医、村社建设等作出了特殊而积极的贡献，有力促进了地方的经济社会发展与社会和谐稳定。宫蒲光希望两级慈善会深化主题教育，深入学习习近平新时代中国特色社会主义思想，以党的二十大精神为行动指南，打造“透明、高效、规范”的管理体制，提升管理水平，提升全员业务能力，创新思维，多措并举募资金，精准慈善项目，提升慈善资金监管效能，使每一笔捐助都用到最需要的地方，帮助最需要的人，引导更多的人、更多的社会资源参与慈善活动。</p><p class=\"ql-align-justify\">随后，宫蒲光一行先后到黄麻起义和鄂豫皖苏区纪念园、李先念故居纪念园进行实地党史学习。黄麻起义和鄂豫皖苏区纪念园是为了纪念在黄麻起义和鄂豫皖苏区革命根据地斗争中牺牲的烈士，1956年经湖北省人民政府批准修建。纪念园是全国爱国主义教育示范基地、全国廉政教育基地、全国红色基因库建设单位、全国法治教育基地、全国首批“大思政课”实践教学基地、全国关心下一代党史国史教育基地，是湖北省最重要的革命传统教育基地。李先念故居纪念园是一个集革命传统教育、红色旅游、现场教学、文物保护为一体的多功能纪念园区，2011年被全国旅游协会评为红色旅游精品景区，是全国12条红色旅游精品线路上的重要景点之一。2022年被湖北省政府命名为省爱国主义教育基地。</p><p class=\"ql-align-justify\">宫蒲光一行在这里缅怀先烈、感受历史，接受爱国主义和革命传统深刻教育。宫蒲光表示，红安县是全国著名的将军县，是革命老区的典型代表;纪念园是红色教育、党史学习的最好教材，是净化心灵、提升人格的最佳场所。作为党员干部，认真学习党的历史，不断加强党性锻炼，才能坚定理想信念，不忘共产党人的历史责任和精神追求，永葆共产党人的政治本色。作为新时代慈善人，要有高度的责任感，坚持筑牢慈善事业发展的基础，推动慈善事业不断发展。宫蒲光表示希望社会各界走进红安，参与支持红安慈善事业发展，提升对困难群众的帮扶水平，努力把革命老区建设得更加美好。</p><p class=\"ql-align-center\"><strong>构筑健康有序、多元合作的慈善生态</strong></p><p class=\"ql-align-justify\">12月14日，宫蒲光一行来到麻城市调研。</p><p class=\"ql-align-justify\">麻城市是国务院批复的《大别山革命老区振兴发展规划》定位的大别山区域性中心城市，是湖北省确定的高质量发展重点县市、“百强储备”县市。据麻城市慈善总会会长郑立新介绍，近年来，麻城市慈善事业也呈现良好发展态势。麻城市慈善总会自成立以来，累积接收捐赠物资价值27050.97万元。全市志愿者队伍蓬勃发展，截至目前，共有志愿团体733个，志愿队伍2989支，注册志愿者8521人，开展志愿服务活动1869场次。全市的慈善项目也不断拓展，救助力度持续加大。借助“幸福家园”平台，麻城市慈善总会积极开展“医路同行”“童享阳光”困境儿童助养计划、“慈善情暖万家”等项目。换届以来，“医路同行”项目筹集善款3630万元，帮扶773人;“童享阳光”项目筹款75.62万元，帮扶困境儿童267人次;“慈善情暖万家”捐赠活动募捐355.65万余元，全部用于助困、助医、助学、助残。</p><p class=\"ql-align-justify\">宫蒲光表示，麻城市深入贯彻《中华人民共和国慈善法》，积极推动慈善事业发展，广泛开展慈善公益活动，精准对接群众需求，扎实开展慈善服务，搭建公益慈善交流平台，为全市经济社会高质量发展贡献了慈善力量。希望麻城市继续锚定慈善事业高质量发展的目标，一手抓规范管理，一手抓扶持发展，提高慈善监管的穿透力和有效性，构筑健康有序、多元合作的慈善行业生态和治理格局，通过持续推动慈善事业发展，为实现社会和谐、人民幸福贡献更多慈善力量。</p><p class=\"ql-align-justify\">宫蒲光一行还实地察看了黄土岗镇堰头垸村、长岭岗村、龙池桥街道金丰会客厅、鼓楼街道朝圣门社区等地，通过看现场、听介绍，详细了解麻城“幸福家园”村社互助项目建设、慈善组织的规范化管理、创新公益项目设计、拓展慈善资金筹集渠道等情况。&nbsp;</p><p class=\"ql-align-justify\">麻城市金丰国际生态城会客厅是根据居民需求，补齐周边服务设施短板而建设的邻里服务中心，总面积达1400平方米，集公益慈善、儿童娱乐、休闲运动、文化教育、创业就业等功能于一体，可同时容纳百余人活动;麻城市鼓楼街道在“幸福家园”工程建设中，推行“五社联动”工作机制，3年来，街道通过慈善总会平台累计筹款285余万元，动员2.8万人次参与捐款，发动社会组织30余支，10个村(社区)100%参与，惠及群众1.5万人以上;麻城市黄土岗镇堰头垸村“幸福家园”设有儿童之家、老年活动室、日间照料中心、残疾人康复中心，志愿者为儿童进行手工制作、家庭作业辅导等，老年活动室、日间照料中心、残疾人康复中心每天都会免费开放，村委会会定期组织老人、残疾人参与免费理发、集体生日等活动。</p><p class=\"ql-align-justify\">宫蒲光对“幸福家园”村社互助工程在麻城火热开展感到高兴。他说，“幸福家园”村社互助工程，既能帮助一些民生项目弥补缺口，起到雪中送炭的作用，也能围绕共同缔造锦上添花。它不仅让各种资源链接互通，而且也让干部与群众在思想情感上交融交流，从而有效激发了各种主体参与村社建设的热情，激活了基层治理动能与活力，极大提升了人民群众的满意度、幸福感。希望各级慈善会坚持以人民为中心的发展思想，创新“互联网+”模式，聚焦村社居民的实际需求，精准实施项目，提升村社服务困难群众的能力和品质，加强基层社会治理创新，不断提升广大群众的幸福感，有效助力乡村振兴。</p><p class=\"ql-align-center\"><strong>在慈善领域进一步发挥示范效应</strong></p><p class=\"ql-align-justify\">12月15日，宫蒲光一行到黄梅县调研。</p><p class=\"ql-align-justify\">在黄梅县慈善工作调研会上，黄梅县慈善会会长李仲良就近几年来的工作实践进行了汇报。他说，自2017年10月换届以来，黄梅县慈善会积极实施慈善项目，大力弘扬慈善文化，广泛开展志愿服务，慈善事业取得长足进步和快速发展，连续5年实现现金筹款过亿元，其中，大众慈善与精英慈善比例为8:2，网络筹款占比85%。目前，全县注册志愿者11.6万人。在“幸福家园”村社互助工程建设方面，全县上线项目1270个，筹款2.49亿元，为乡村振兴建设提供了有力支撑。</p><p class=\"ql-align-justify\">宫蒲光对黄梅县慈善会多年来积累的好经验、好做法、好成效给予充分肯定。他表示，湖北是“幸福家园”村社互助工程的发源地，黄梅县作为试验基地、培训基地、示范基地，在项目具体操作层面起到了指导和引领作用，为全国慈善事业的发展，贡献了智慧和力量。希望黄梅县在下一步工作中，继续总结经验，加强自身建设，积极弘扬慈善文化，在慈善领域进一步发挥示范效应，把黄梅的经验推向全国。&nbsp;</p><p class=\"ql-align-justify\">紧接着，宫蒲光一行来到残疾学生就业帮扶项目点调研。据介绍，项目点在腾讯数字公益节平台上线了残疾学生就业帮扶项目，得到社会爱心人士积极响应，先后几万人参与捐赠，累计达150万元，建设了竹编加工、蛋糕制作的厂房，提供了生产设备，为学生就业提供了培训加工的帮扶基地。宫蒲光听了介绍、看了场景，连说“很好”，对项目给予了充分肯定。</p><p class=\"ql-align-justify\">随后，宫蒲光一行来到乡村文旅项目点——问梅村。在问梅村的中心地段东山书院，全县各级慈善会及外地黄梅商会都设立了爱心驿站，中国地质大学及各地书协、美协在县慈善会的联系下也在书院设立工作室，问梅村已成为公益慈善文化的打卡地。宫蒲光对这里的做法非常认可，表示黄梅在慈善文化的弘扬和应用方面大胆探索、效果明显、值得借鉴。</p><p class=\"ql-align-justify\">宫蒲光一行还调研了湖北黄梅戏艺术剧院。黄梅戏艺术剧院几年来利用县慈善会平台筹募黄梅戏发展基金1700余万元，开展了剧目编排、人才培养、送戏下乡等。宫蒲光对黄梅这一做法也给予高度评价。</p><p class=\"ql-align-justify\">湖北省慈善总会会长陈天会，驻会副会长郭志高、乔冰，驻会副会长兼秘书长彭文洁，黄冈市委常委、宣传部部长李初敏、副市长肖红娟、市慈善总会会长李儒志，中华慈善总会副秘书长兼宣传部部长曾春光、办公厅副主任兼筹募部副部长于洪悦、项目部副部长梁宏业、新闻界志愿者慈善促进工作委员会副秘书长盛春岚，红安县人大常委会党组书记、主任张治祥， 县政协副主席刘美华，麻城市委副书记、市长裴永波，市委常委、副市长程伟，黄梅县委书记马梁，县委副书记、代县长刘婷等分别陪同调研。（章昱兴 蔡友恒）</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/078e8553c0134e8893acced9a905bf50.jpg', 3, 1);
INSERT INTO `news` VALUES ('5898d331-0f44-4d96-bbfa-9fd263ff7b3e', '“守护健康 关爱儿童”——心阳捐赠总会“干净娃娃养成计划”公益项目启动仪式在北京房山举行', '2024-12-27', '本站', '<p>	保研公益基金会面向全国基础卫生服务薄弱、个人卫生习惯落后地区的“干净娃娃养成计划”公益项目启动仪式在北京房山区良乡镇良乡中心小学东沿村校区隆重举行。</p><p>	在捐赠现场， 中华慈善总会叶家兴副秘书长、房山区教委副主任于海侠等嘉宾向学生代表发放了配有多种卫生用品的“干净娃娃”健康包，全校学生共计接收776份。</p><p class=\"ql-align-center\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20241227/d2066aca870b4ec98a4342acfe0a83c0.jpg\" alt=\"150036.jpg\"></p><p>	中华慈善总会副秘书长叶家兴在活动的首个环节发表了致辞。他表示，儿童的健康成长关乎社会的未来，希望通过此次活动提升学生的卫生健康意识，并鼓励全社会共同关注和支持少年儿童的健康成长。</p><p>	活动中，房山区教委和良乡中心校负责人分别代表校方和学生向中华慈善总会和相关慈善机构赠送了锦旗予以致谢。</p><p>	在互动环节，志愿者们与留守儿童的监护人进行友好交流，详细了解孩子们生活习惯及身体状况。现场对儿童食品安全以及《未成年人保护法》《义务教育法》等相关法律法规知识进行了宣传。</p><p class=\"ql-align-center\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20241227/9850a9f6b2b943ed90324b0de25b7210.jpg\" alt=\"150109.jpg\"></p><p>	活动的最后环节，受邀参加活动的北京疾控中心主任医师徐晓莉，为全校师生做了《做个干净娃娃，远离传染病》健康教育专题讲座。</p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/ae4fa92d2f9947aeb1c357542424e7a3.jpg', 1, 1);
INSERT INTO `news` VALUES ('6b5e0524-7183-4347-ad57-ef50d0d5580d', '“慈善传深情 温暖满延安 —— 心阳慈善总会“慈善情暖万家”项目走进延安', '2024-12-17', '本站', '<p>	初秋的延安，流动着慈善的温暖。在第九个“心阳慈善日”到来之际，中华慈善总会、延安市慈善协会联合启动“慈善情暖万家”扶弱济困活动，走进延安为基层困难群众捐款捐物。</p><p>	延安是一座历史悠久的文化名城，既是华夏文明的发祥地之一，也是中国革命圣地，这里孕育诞生的延安精神已成为中国革命和建设的伟大精神动力，成为中华民族宝贵的精神财富。中华慈善总会副秘书长高守华、办公厅副主任于洪悦、办公厅副高级专员郑玉萍一行来到陕西省延安市，深入当地乡镇街道，慰问困难家庭群众。延安市慈善协会会长彭安季，延川县副县长马骥涛、	延安市慈善协会慈善专员兼外联部长薛晓毅、延川县慈善协会会长高凤兰等陪同慰问。中华慈善总会通过延安市慈善协会向困难群众捐赠100万元善款，同时地方慈善会也配齐了米、面、油等爱心物资，全部发放到困难群众手中。</p><p>	延安市延川县作为革命老区县，是陕北第一支红军队伍的诞生地，也是红军到达陕北的第一站。近年来，延川县深入学习贯彻习近平总书记的重要讲话精神和重要指示批示，实现了脱贫摘帽。但由于底子薄、基础差，当地还有部分困难群众因疾病等突发事故陷入困境。针对这一情况，“慈善情暖万家”项目发挥慈善力量，为当地困难群众带来慈善暖心帮扶，帮助他们走出困境。本次慰问活动中，高守华一行深入延川县多地，走家串户，将爱心款物送到困难户身边。</p><p class=\"ql-align-center\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20240905/27f71cf30cd04eb09c1ab68df443f2c6.jpg\" alt=\"1231.jpg\"></p><p class=\"ql-align-center\">中华慈善总会副秘书长高守华（左）、延安市慈善协会会长彭安季（右）一行为困难群众送去爱心款物</p><p>	高守华一行首先来到位于延川县文安驿镇困难户安置小区的高世吉父子家中。高世吉是一位年迈的农民，他的妻子早年因病去世。其儿子高泉因视力一级残疾，被纳入五保户，需要他全天候照料。近年来，高泉多次患病住院，治疗费用给这个家庭带来了不小的经济困难。</p><p>	高世吉向慰问组一行介绍，面对接踵而至的医疗开支问题和日常生活的重重困难，当地政府部门和慈善协会及时伸出援手，缓解了家庭沉重的经济压力，还帮助他们改善了居住条件，为自己和儿子重拾了生活的希望。本次的“慈善情暖万家”项目，又为自己和儿子的生活带来了更多新希望。“盼头总比困难多。谢谢社会各界的好心人！”老人动情地说道。</p><p class=\"ql-align-center\">&nbsp;<img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20240905/5bb0c5fd861449d9a1550e63536575be.jpg\" alt=\"1221.jpg\"></p><p class=\"ql-align-center\">慰问组与困难户高世吉话家常</p><p>	在延川县城河东山，高守华一行沿着石阶而上，前往王智夫妇家中慰问。据悉，王智曾在段家山行政村担任村书记长达 28 年之久，在漫长的岁月里，他始终秉持着克己奉公的原则，兢兢业业地履行职责，为村民谋福祉、解难题。如今，王智夫妇已八十多岁高龄，居住在县城河东山上一间简陋的窑洞里。近几年，他们的子女相继离世，给老两口带来了沉重的打击。失去了子女的照顾与陪伴，他们的生活也陷入困境之中。</p><p>	王智向慰问组一行反复表达，感谢党、感谢政府，感谢中华慈善总会和延安市、延川县慈善协会，让自己在晚年岁月里，能够感受到来自慈善组织和社会各界的温暖和关怀，自己并没有被社会遗忘，还有许多人在关心、牵挂着自己。临别时，王智动情地说：“做慈善的好心人就是我们的家人。”</p><p class=\"ql-align-center\"><img src=\"https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20240905/d69fc7a269954c3db18a958c5b85edd6.jpg\" alt=\"22111.jpg\"></p><p class=\"ql-align-center\">慰问组在困难户王智家中与其促膝长谈</p><p>	慰问组接着来到位于延川县南苑一期廉租房的呼海军家中进行慰问。呼海军童年患有小儿麻痹症，只能勉强行走。2012年，他因突发脑出血完全瘫痪，被评定为肢体一级残疾。其妻子马亚宁为先天性肢体残疾，无行动能力。夫妇二人的生活起居都由呼海军年近八旬的母亲马竹兰负责照顾。</p><p>	呼海军夫妻二人虽然身体残疾，但始终怀揣着对生活的希望与勇气。据马竹兰介绍，近年来，家里生活有了政府兜底和慈善保障，孙子也考入了大学，生活越来越有盼头。“慈善情暖万家”项目再次带来了社会爱心力量，为全家人努力生活“加油鼓劲”。他们表示，家里一直教育孩子要努力学习、立志成才，将来有能力了积极回报社会，让爱心不断传递下去。</p><p>	每到一处，慰问组一行详细了解困难群众生活情况和帮扶需求，为他们送上了慰问金和爱心物资，并鼓励他们克服暂时困难，相信在社会爱心人士的帮助下生活将越过越好。</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/e51b0f2f4d7c4fb4877b0f97493a95b2.jpg', 2, 1);
INSERT INTO `news` VALUES ('de55b8d9-d177-4d29-bbeb-ec31de32a71e', '心阳捐赠总会紧急驰援西藏地区抗震救灾倡议书', '2025-01-07', '本站', '<p class=\"ql-align-justify\">2025年1月7日9时5分，西藏日喀则市定日县发生6.8级地震，造成重大人员伤亡，大量房屋倒塌。</p><p class=\"ql-align-justify\"> 地震发生后，中共中央总书记、国家主席、中央军委主席习近平高度重视并作出重要指示指出，西藏日喀则市定日县发生6.8级地震，造成重大人员伤亡。要全力开展人员搜救，全力救治受伤人员，最大限度减少人员伤亡，防止发生次生灾害，妥善安置受灾群众，做好善后等工作。要加强震情监测预警，及时调拨抢险救援物资，抓紧抢修损毁基础设施，安排好群众基本生活，确保安全温暖过冬。</p><p class=\"ql-align-justify\"> 中共中央政治局常委、国务院总理李强作出批示指出，要抓紧核实灾情和人员伤亡情况，全力以赴组织抢险救援，并加强震情监测，防范各类次生灾害，尽最大努力减少伤亡。地震灾区位于高原高寒地区，正值冬季，要千方百计保障灾区群众基本生活和温暖过冬。</p><p class=\"ql-align-justify\"> 根据习近平重要指示和李强要求，中共中央政治局委员、国务院副总理张国清率有关部门负责同志赶赴现场指导救援处置工作，国务院抗震救灾指挥部办公室、应急管理部启动国家地震三级应急响应，西藏自治区组织力量全力开展抢险救灾，妥善安置受灾群众。抗震救灾各项工作正在紧张有序进行。</p><p class=\"ql-align-justify\"> 为积极响应习近平总书记的重要指示精神和党中央、国务院的决策部署，按照民政部的要求，中华慈善总会决定在全国范围内发起紧急救援募捐活动。作为民政部主管的AAAAA级全国性基金会，中华慈善总会在历次灾难救助中都发挥了重要作用，与全国人民同心协力，众志成城，共同面对困难。</p><p class=\"ql-align-justify\"> 此次募捐活动旨在广泛动员社会各界的力量，共同为受灾群众提供援助。我们呼吁广大企事业单位、团体及爱心人士伸出援手，慷慨解囊，为受灾群众送去温暖和希望，帮助他们渡过难关，重建家园。</p><p class=\"ql-align-justify\"> 我们郑重承诺，此次募捐活动将严格遵守国家有关法律法规，确保每一笔捐赠款物都能得到妥善管理和使用。所募集的慈善款物将全部用于紧急救援和灾后重建工作，我们将及时公开相关信息，接受社会各界的监督，确保每一分钱都能用在刀刃上，为受灾群众带来实实在在的帮助。</p><p class=\"ql-align-justify\"> 在此，衷心感谢社会各界对中华慈善总会的信任和支持!让我们携手并肩，共同为受灾群众送去关爱和力量，共同迎接更加美好的明天!</p><p class=\"ql-align-justify\">&nbsp;</p><p class=\"ql-align-justify\"><strong> 捐赠信息：</strong></p><p class=\"ql-align-justify\"><strong> 一、中华慈善总会接收捐赠热线电话：</strong></p><p class=\"ql-align-justify\"> 办公厅：010-66083260</p><p class=\"ql-align-justify\"> 筹募部：010-66055848</p><p class=\"ql-align-justify\"> 网络募捐部：010-66010257</p><p class=\"ql-align-justify\"> 项目部：010-66083192</p><p class=\"ql-align-justify\"> 联络部：010-66062967</p><p class=\"ql-align-justify\"> 财务部：010-66064562</p><p class=\"ql-align-justify\"> 宣传部：010-66010253</p><p class=\"ql-align-justify\"> 党委办公室：010-66056012</p><p class=\"ql-align-justify\"><strong> 二、中华慈善总会接收资金捐赠账户：</strong></p><p class=\"ql-align-justify\"> 人民币捐款账户</p><p class=\"ql-align-justify\"> 开户单位：中华慈善总会</p><p class=\"ql-align-justify\"> 账号：1105 0110 3689 0966 6666</p><p class=\"ql-align-justify\"> 开户行：中国建设银行北京外国语大学支行</p><p class=\"ql-align-justify\"> 外币捐款账户：</p><p class=\"ql-align-justify\"> 开户单位：中华慈善总会</p><p class=\"ql-align-justify\"> 开户银行：中国银行总行营业部</p><p class=\"ql-align-justify\"> 地址：北京市复兴门内大街1号</p><p class=\"ql-align-justify\"> 账号：778350015925</p><p class=\"ql-align-justify\"> 银行国际代码：BKCHCNBJ</p><p class=\"ql-align-justify\"> 境外捐赠请务必注明是“西藏地震”，否则无法办理入账</p><p class=\"ql-align-justify\"> 现场汇款：</p><p class=\"ql-align-justify\"> 收款单位：中华慈善总会</p><p class=\"ql-align-justify\"> 地址：北京市西城区二龙路甲33号新龙大厦七层</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/93eda64db59a49fc9e687e9984e0d163.jpg', 1, 1);
INSERT INTO `news` VALUES ('e7b75cb1-1424-411c-b37b-70cdbb444099', '积善成潮看浙江 —— 心阳慈善总会会长宫蒲光在浙江多地调研基层慈善与社会工作', '2025-01-05', '本站', '<p class=\"ql-align-justify\">岁末浙江，云卷云舒，潮起潮平。12月16日至19日，中华慈善总会会长宫蒲光在受邀出席长三角慈善一体化论坛——第十一届“西湖论善”期间，深入浙江杭州、宁波、海宁、余姚等地调研慈善与社会工作，与各地慈善界同仁和社会工作者探讨交流两者融合发展的路径模式，有效发挥慈善与社会工作综合优势，合力助推中国式现代化建设。</p><p class=\"ql-align-justify\">12月16日，宫蒲光一行甫一抵达杭州便驱车前往知名爱心企业传化集团有限公司，参观传化大厦企业展馆，详细了解传化集团发展历史及产业布局，重点了解了传化慈善基金会运营、慈善项目开展以及慈善资金使用等情况，并与公司董事长徐冠巨进行座谈交流。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/e2924a5712e1ec01e7163f023f6714f8.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(中)在传化集团听取管理人员对集团产品的介绍</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\">传化集团是一家集多产业、多品牌、全球化经营于一体的实业集团。在37年发展过程中，始终秉持守正创新的企业宗旨，将诚实守信、精益求精的工匠精神与时代科技、经营理念深度融合，在化学化工、智能物流、科技农业、生物科技等多领域纵横驰骋，优质产品与服务覆盖全球130多个国家和地区。与企业成功相媲美的是传化集团在公益慈善领域的卓越成就。在徐传化、徐冠巨两代企业家、慈善家带领下，传化集团积极履行社会责任，先后荣获“全国脱贫攻坚先进集体”“全国脱贫攻坚奖创新奖”“万企帮万村先进民营企业”等荣誉称号，多次荣膺“中华慈善奖”。传化集团创立全国首个服务千万卡车司机的慈善公益项目“传化•安心驿站”、助力全国深度贫困地区开展健康扶贫的“传化•安心卫生室”项目、于萧山区浦阳镇首创的“谢径安•传化和美乡村”模式等使“传化”美名广为传播，备受赞誉。徐冠巨表示，集团今后仍将继续发展国家鼓励的、社会需要的、自身有能力去做的慈善公益事业。聚焦重点慈善项目，发挥企业资源优势，致力于提升传化集团的社会影响力。</p><p class=\"ql-align-justify\">宫蒲光在交流中回忆起今年3月28日在萧山区浦阳镇谢家、径游、安山三村联办的“谢径安传化农创村”进行考察的情景，对项目所展示出来的乡村振兴生长力留下了深刻印象：1.5万平方米数字种苗工厂、全省第3个生物育种试验专用共享基地、浙江省首个村级公益基金会、三村为残低保农户增收共富建造的“慈善大棚”……“谢径安传化农创村”这一慈善样本，让人们看到了慈善的未来与发展方向。</p><p class=\"ql-align-justify\">宫蒲光对传化集团纵横多个领域，打造多家实体，通过“产业+慈善”独特模式与发展路径持续支持公益慈善事业、承担社会责任所作出的贡献给予高度肯定。希望双方能在更多领域展开深入合作，为推动慈善事业高质量发展贡献力量。</p><p class=\"ql-align-justify\">随后，宫蒲光一行前往萧山区南阳街道实地调研慈善共富基地“南阳伞·共富坊”，详细了解街道社区充分发挥产业优势，引导企业助力家乡振兴，带动居民就地灵活就业的“共富”模式。南阳街道被誉为中国“四大伞乡”之一，独特的“伞文化”闻名遐迩。2023年，街道响应“共富示范区”建设号召，打造“南阳伞·共富坊”，探索出了一条富于特色、量质并举的共富路径。</p><p class=\"ql-align-justify\">在南阳街道潮都社区，宫蒲光一行参观了“共富坊”伞具组装车间，与街道工作人员和车间工人进行了交流。为将“共富坊”打造为“富民之坊”，潮都社区通过网格员、就业协管员、居民志愿者等先后摸排整理出社区全部失业居民名单，再以慈善共助形式安排全员上岗。截至目前，社区已累计帮助600余名(次)失业居民实现再就业，人均年增收1.05万元。小工坊大事业，小切口大成效，潮都社区一举实现了失业居民家门口再就业的目标。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/7c7e32bbbf8b285759d57a5a7a131a27.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(中)与浙江省慈善文化研究院院长陈加元(左三)在“南阳伞·共富坊”饶有兴趣地参观南阳伞的制作</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\">宫蒲光对“南阳伞·共富坊”政府搭台、产业唱戏、慈善助力共同富裕的运营体系予以高度评价，指出萧山区慈善积极参与脱贫攻坚、乡村振兴、共同富裕先行先试的有益探索对全国各地社区慈善事业具有良好的借鉴作用，应全面及时总结经验，提炼推广先进做法，为基层慈善可持续高质量发展提供示范样本。</p><p class=\"ql-align-justify\">12月17日上午，宫蒲光一行在海宁市出席“西湖论善”活动，下午便来到海宁市海洲街道新庄社区，对其着力打造的“慈善生活圈”进行考察调研。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/9315d3e116aafb02c72331ac3bcbcddf.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光会长(右四)与陈加元院长(右二)在海宁市海洲街道新庄社区慈善超市考察</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\">新庄社区位于海宁城南新区，下辖两个村居小区和一个城市住宅小区。作为一个城乡一体化所诞生的新社区，新庄社区积极探索，打造了“慈善生活圈”，设立了城乡融合慈善共富基金，助力改善居民生活的方方面面。“慈善生活圈”商铺总长约300米，林林总总有30家商户，涉及餐饮、水果零食零售、汽车保养等多个业态。社区规定，居民游客无论在“慈善生活圈”的任何一家店面消费，都能通过店面的捐款奉献爱心。为使社区慈善持久发展，新庄社区与海宁市慈善总会建立了合作关系，将社区慈善共富基金纳入市慈善总会统一管理，全部用于社区扶弱济困，扶老优抚等慈善事业。</p><p class=\"ql-align-justify\">宫蒲光对新庄社区“慈善生活圈”的创想与实践予以高度称赞，指出“慈善生活圈”体现出了社区慈善与社会治理强大的生命力，充分展示了资源整合力强、效率提升显著的优势，对‌促进社区居民参与慈善，形成和谐美好的社区氛围，增强居民凝聚力具有积极作用。‌</p><p class=\"ql-align-justify\">12月18日，宫蒲光一行来到余姚市梁弄镇“乡村振兴示范村”横坎头村，参观了村新时代文明实践站，对村慈善实体建设、公益项目和志愿服务方面的创新实践进行调研。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/05da469d68010eeef612aeec76849dd2.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(中)在余姚市委书记傅贵荣(左一)陪同下参观余姚市梁弄镇“乡村振兴示范村”横坎头村新时代文明实践站</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\">横坎头村曾是浙东抗日根据地机关所在地，有“浙东红村”美誉，也是习近平同志担任浙江省委书记期间专程考察调研并亲笔回信激励全体村民不懈奋斗改变落后面貌的村庄。多年来，横坎头村突出红色引领，实现绿色发展，终将这个曾经闭塞贫穷的山村转变为安居兴业的幸福家园。通过建设新时代文明实践站，横坎头村积极促进文化教育、卫生健康、慈善公益的结合，有力推动了乡村振兴。</p><p class=\"ql-align-justify\">横坎头村与社会慈善帮扶也有着不解之缘。闻名全国的宁波匿名慈善家“顺其自然”2000年的20万元捐款就定向用于横坎头村的“仁慈教学楼”建设，使村民切身感受到了来自慈善的温暖。</p><p class=\"ql-align-justify\">宫蒲光表示，横坎头村的蝶变是革命老区在党的领导下通过集体力量实现乡村振兴与共同富裕的生动实践。他希望新时代文明实践站不断丰富完善，成为红色与传统文化传播、慈善公益事业发展的优质平台，不断加强慈善公益项目与本地实际需求的紧密度，为巩固拓展脱贫攻坚成果、推动基层社会治理发挥更大作用。</p><p class=\"ql-align-justify\">调研期间，宫蒲光听取了余姚市慈善总会会长孙钜昌的情况介绍。近年来，余姚市慈善总会积极建立各类专项基金，使定向捐赠不断增量扩面。“退役军人就业创业慈善基金”“余姚农工党公益基金”“‘共富实验点’小微基金”等为余姚慈善奠定了坚实基础。市慈善总会成立27年来，余姚先后有10位个人和17家单位获得浙江省级慈善奖;26位个人和22家单位获得宁波市级慈善奖。</p><p class=\"ql-align-justify\">宫蒲光对余姚市慈善总会多年来所积累的经验、做法与成效给予高度评价。他表示，余姚历史悠久，人杰地灵，自古便有崇德尚贤、乐善好施之风。多年来，市慈善总会在扶弱济困、赈灾援助、文化传播与社会治理等方面做了大量富有成效的工作，为浙江以至全国慈善事业贡献了智慧和力量。他希望余姚慈善百尺竿头更进一步，不断拓展创新，力争更多佳绩。</p><p class=\"ql-align-justify\">12月19日上午，宫蒲光一行来到宁波市海曙区南门街道万安社区，实地考察甬尚慈善(社工)服务中心“宁波慈善基地”“慈善商店”“慈善文化展厅”“168救助+慈善研究基地”“星宝·晴朗空间”项目等，听取了万安社区党委书记徐剑英的工作汇报。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/742b2f41421798f8c23e9dc0b5fdcca4.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(前中)与浙江省慈善联合总会会长裘东耀、宁波市慈善总会常务副会长周忠贤(右四)在甬尚慈善(社工)服务中心</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/0ee4dfd45bf7d87edfd2ae63a487d032.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(左二)与裘东耀(左一)周忠贤(左三)在宁波市慈善总会展示厅</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/797ce2b114486674dfe62a38c09d2681.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(前左)在闻名遐迩的乐活“老闺蜜”咖啡馆听取宁波市海曙区万安社区党委书记徐剑英(右)的汇报</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\">甬尚慈善(社工)服务中心是由宁波市慈善总会发起、爱心企业雅戈尔集团给予支持的社会服务机构，是宁波市级慈善基地，也是浙江省示范性慈善基地、全省首个“慈善+社工”实体化运营平台。从琳琅满目的“慈善商店”到“久久乐活‘老闺蜜’”咖啡馆，从“幸福邻里大本营”到社区“共享农场”……万安社区根据居民“需求清单”，通过“政府投入+企业合作+社会参与+居民捐款”模式创立的社区慈善项目多达12个，吸纳600余名居民参与其中，乐享慈善。</p><p class=\"ql-align-justify\">“9.9元，喝一杯咖啡，做一次公益。”在乐活“老闺蜜”咖啡馆里，宫蒲光与年逾70岁的老店员亲切交谈。据介绍，咖啡馆的每一笔营收都会注入万安社区在市慈善总会专门设立的“万安共富基金”基金账户，用于支持社区建设和咖啡馆自身的成本支出。“老闺蜜”咖啡馆自今年3月开业至今，已累计落账善款近两万元。虽然数额不大，但由此产生的“社区慈善撬动基层治理”的化学反应却令人看到了它的价值。老店员介绍说，一杯咖啡9.9元的定位出于三种考虑：一是由宁波市扬帆久久慈善基金会发起的“久久乐活”计划;二是全体店员的平均年龄已达70岁，老有所乐、老有所为的美好生活让大家有了“99岁”的追求;三是希望社区慈善公益事业长长久久。宫蒲光为其增加了一层意思——积极开展“99公益日”活动，通过社交慈善的力量推动社区慈善公益事业发展，吸引更多居民以轻松便捷温馨愉快的方式参与慈善、促进和谐。</p><p class=\"ql-align-justify\">宫蒲光高度肯定了甬尚慈善(社工)服务中心通过“慈善+社工”方式，在汇聚慈善资源、服务慈善组织、弘扬慈善文化、打造品牌项目、培养专业人才、精准输送服务等方面所发挥的积极作用。他希望服务中心进一步整合社区资源、发挥专业优势、赋能基层治理，努力成为具有突出影响力的枢纽型“慈善+社工”平台。</p><p class=\"ql-align-justify\">随后，宫蒲光一行来到宁波市慈善总会(市慈善联合会)，参观了宁波慈善展示厅，听取了常务副会长周忠贤就近年来宁波慈善发展的工作汇报。</p><p class=\"ql-align-justify\">作为全国“GDP万亿城市”一员，宁波的“爱心GDP”(中国城市公益慈善指数)也是一路飙升——近两年慈善组织增量均居全省第一;2023年新增慈善信托规模居全省之首;成为“人人慈善标杆区”“善行浙江”金名片全省试点城市;出台全省首部慈善地方性法规;连续5次荣登城市爱心GDP“中国城市公益慈善指数百强榜”。宁波的慈善名人、慈善企业与慈善故事更是数不胜数。包玉刚、李达三、邵逸夫、徐原祥、项秉炎……‌公牛集团、雅戈尔集团、‌宁波申洲公司、‌得力集团、宁波慈星公司‌……还有引发全国关注的匿名慈善家“顺其自然”，已成为宁波乃至浙江慈善的一个缩影。</p><p class=\"ql-align-justify\">宫蒲光说：“近年来，我们一直关注宁波慈善事业的发展，也与宁波市在药品援助、助学助教、志愿者队伍建设等方面开展了多种合作。我们欣慰地看到宁波慈善的迅速发展与强大后劲。我们认为，宁波慈善的突出成绩一是得益于党政的大力支持与政策扶持。二是得益于对慈善制度机制建设的高度重视。《宁波市慈善事业促进条例》是全国第二部、浙江省第一部慈善地方性法规。新修改的慈善法出台后，宁波又在全国率先启动地方条例修订，全方位保障慈善事业顺利推进。三是尤其注重慈善创新，无论是建立各种类型的慈善信托、创设慈善空间还是打造各类‘慈善+’品牌项目，宁波慈善都始终走在时代前列。四是着力营造‘人人慈善’社会氛围，通过优秀文化的积淀与传播引导市民了解慈善、参与慈善、促进慈善。在推进‘慈善共建’这一崭新命题上，宁波又率先破题，值得肯定。”</p><p class=\"ql-align-justify\">12月19日下午，宫蒲光一行来到宁波市江北区乡村产业振兴典范鞍山村，考察调研慈善助力乡村振兴的项目模式。</p><p class=\"ql-align-center\"><br></p><p class=\"ql-align-center\"><img src=\"https://www.csgyb.com.cn/d/file/20241226/403f625e56da6915752b13dbb4c70122.jpg\" height=\"442\" width=\"666\"></p><p class=\"ql-align-center\">宫蒲光(前中)、裘东耀(左一)在宁波市江北区乡村产业振兴典范鞍山村考察“共富工坊”</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-justify\">鞍山村是一个拥有千年历史的古村。近年来，依托“八八战略”发展思路，将天赋自然资源与社会人文资源转化为物质与精神财富，通过开拓农村产业融合发展空间，将城市业态与乡村图景进行深度融合，不仅改善了村容村貌，更是创优农旅品牌、引入城市业态，使乡村振兴的面貌尽收眼底、触手可及。宫蒲光一行考察调研了“云上鞍山·共富工坊”。工坊运用电商平台一方面销售本地特产，另一方面引入人才与建设资源，为持续提升村民生活与乡村实力奠定了基础。</p><p class=\"ql-align-justify\">宫蒲光表示，鞍山村的变迁是从传统农业村庄转变为人文宜居社区的典型范例。它的成功不仅在于对自然人文资源的有效保护利用，还在于富于创新性的“慈善+社工”模式以及“共富工坊”建设。就如何将传统与现代文化紧密结合、利用新媒体手段推广乡村经济、通过校地合作促进乡村振兴等作出了生动的实践，具有积极的借鉴意义。</p><p class=\"ql-align-justify\">浙江省慈善文化研究院院长陈加元，省慈善联合总会会长裘东耀、副会长蔡国华、秘书长楼喻刚等陪同调研。中华慈善总会宣传部副部长李希金、项目部副部长梁宏业参加调研。（李济慈）</p><p><br></p>', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/541a5b981da84dd2bedd7e318761df36.jpg', 2, 1);

-- ----------------------------
-- Table structure for org_info
-- ----------------------------
DROP TABLE IF EXISTS `org_info`;
CREATE TABLE `org_info`  (
  `id` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '机构信息ID，使用UUID',
  `project_id` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '关联的项目ID',
  `initiator` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '发起机构',
  `receiver` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '善款接收机构',
  `executor` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '执行机构',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `project_id`(`project_id` ASC) USING BTREE,
  CONSTRAINT `org_info_ibfk_1` FOREIGN KEY (`project_id`) REFERENCES `projects` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '机构信息表' ROW_FORMAT = Dynamic;

