-- ----------------------------
-- Table structure for projects
-- ----------------------------
DROP TABLE IF EXISTS `projects`;
CREATE TABLE `projects`  (
  `id` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '项目ID，使用UUID',
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '项目封面图片URL',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '项目标题',
  `record_number` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '备案编号',
  `target_amount` decimal(15, 2) NOT NULL COMMENT '目标金额',
  `raised_amount` decimal(15, 2) NOT NULL COMMENT '已筹金额',
  `donation_count` int(11) NOT NULL COMMENT '爱心次数',
  `start_date` date NOT NULL COMMENT '项目开始日期',
  `end_date` date NOT NULL COMMENT '项目结束日期',
  `details` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '项目详情（HTML内容）',
  `category` tinyint(4) NOT NULL COMMENT '项目分类（0: 扶贫济困, 1: 健康医疗, 2: 助老扶幼, 3: 文化教育, 4: 社会服务, 5: 科技环保, 6: 灾难救助, 7: 专项基金）',
  `is_recommended` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否推荐（0: 不推荐, 1: 推荐）',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '项目表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of projects
-- ----------------------------
INSERT INTO `projects` VALUES ('109b1338-8dc7-4fcb-9bb5-2701b2f0b208', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/0cd21fe8ecb14c738a35666b883d789c.png', '善济病困项目', '53100000500016260MA21001', 5000000.00, 2600.00, 2, '2021-01-16', '2025-12-31', '致力于为贫困患者提供医疗援助，通过筹集善款和资源，帮助无力承担高昂医疗费用的家庭渡过难关。项目涵盖重大疾病治疗、手术费用、康复支持等，确保每一位患者都能获得及时、有效的救治。我们与多家医院和慈善机构合作，确保善款透明使用，让每一份爱心都能切实帮助到需要的人。', 1, 0);
INSERT INTO `projects` VALUES ('2086e217-8455-4636-8863-6eb5b3c969ef', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/1792d4a204c14c93bd29c644f6abde7a.jpg', '心阳捐赠总会善济病困工程•关爱白内障患者项目', '53100000500016260MA22008', 300000.72, 3300.00, 3, '2022-10-11', '2025-12-31', '2022年10月，中华慈善总会联合中国乡村发展志愿服务促进会共同发起设立“中华慈善总会善济病困工程•关爱白内障患者项目”，旨在帮助更多农村白内障患者实施复明手术，解决其因病致盲问题，助力乡村振兴，促进共同富裕。', 1, 0);
INSERT INTO `projects` VALUES ('f220f80c-e4e1-42f7-bd5b-ab51d9538560', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/projects/20160325092036_3732.jpg', '慈善文化进校园项目', '20231008001', 12000.00, 3500.00, 2, '2023-01-03', '2025-12-31', '2011年，中华慈善总会基于慈善文化教育对青少年身心健康发展的良好作用，启动了“慈善文化进校园”项目。这是中华慈善总会联合各地慈善会、教育部门共同实施的全国性慈善文化普及项目。以赠阅《慈善读本》为载体，在中小学校开展专题阅读、主题活动和课外实践，使孩子们在多彩的团体活动中，肯定自我和他人，积极开朗学习生活，弘扬慈善精神，播散爱心种', 3, 0);
INSERT INTO `projects` VALUES ('f2c46e1f-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/c2561ced55b3400cb15b1f47ba2bc8a3.jpg', '“聚爱启善”志愿服务行动', '20231001001', 100000.00, 4000.00, 1, '2024-11-01', '2025-12-31', '开展慈善志愿服务项目，一是为参加中华慈善总会志愿服务活动的志愿者购买人身意外伤害保险，给予一定的补贴，褒扬激励、志愿服务培训等多方面的人文关怀。二是应急救援志愿服务组织或个人提供物资及资金支持。', 0, 1);
INSERT INTO `projects` VALUES ('f2c47854-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/20191030151348_9712.jpg', '慈善情暖万家”扶弱济困项目', '20231002001', 200000.00, 2600.00, 3, '2023-10-02', '2025-12-31', '“慈善情暖万家”活动是由中华慈善总会牵头，并发动全国各地慈善会共同实施的一个大型慈善项目。该项目通过充分动员社会各界捐款捐物、开展志愿服务，为身处困境的基层贫困群众送去物资支持和精神慰藉，鼓励他们重树生活信心、自强自立、逐渐摆脱生活困境。', 0, 1);
INSERT INTO `projects` VALUES ('f2c47cd8-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/20140912093823_4142.jpg', '微笑列车唇腭裂修复慈善项目', '20231004001', 300000.00, 4900.00, 2, '2023-10-03', '2025-12-31', '微笑列车项目自1999年3月由中华慈善总会组织承办以来，通过与各省慈善会密切合作，不断完善项目管理方式，广泛开展项目宣传活动，为贫困患者补贴交通食宿费用，为部分合作医院补贴手术款等，救治地域不断扩大，救治人员不断增多，得到了社会各界的广泛赞誉和认可。', 1, 1);
INSERT INTO `projects` VALUES ('f2c47e5e-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/54037366e56e42efbc441048c138d123.jpg', '圆梦中国慈善行动', '20231005001', 500000.00, 1150.00, 2, '2023-10-05', '2025-12-31', '圆梦中国行动项目是经过中华慈善总会审核通过的一个具有公募资质的救助项目。项目以贯彻落实中央文化帮扶政策为宗旨，以社会主义核心价值观为引领，以革命老区为重点，加大文化帮扶力度，发挥文化在帮扶工作中“扶志”“扶智”作用，助力革命老区文化建设快速发展，用实际行动帮扶革命老区巩固脱贫攻坚成果，助力乡村振兴的全面推进。', 0, 0);
INSERT INTO `projects` VALUES ('f2c47fcc-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/20141020174101_7307.jpg', '慈善一元捐行动', '20231006001', 50000.00, 3400.00, 2, '2023-10-06', '2025-12-31', '慈善一元捐行动是中华慈善总践行社会捐赠日常化儿开展的面向全社会的小额募捐行动。慈善一元捐行动是以人民币一元为起点，在不影响捐赠者生活水准的前提下进行捐赠，多捐不限，其意义在于最大限度地激发每个人的慈善之情，弘扬“不以善小而不为”的慈善理念，使每个人的爱心通过“慈善一元捐行动”得以实现。', 0, 1);
INSERT INTO `projects` VALUES ('f2c48154-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/7e16c8dee4e946828bba8e5efcf6dcd8.jpg', '一张纸献爱心', '20231007001', 80000.00, 2200.00, 2, '2023-10-07', '2025-12-31', '慈善一元捐行动是中华慈善总践行社会捐赠日常化儿开展的面向全社会的小额募捐行动。慈善一元捐行动是以人民币一元为起点，在不影响捐赠者生活水准的前提下进行捐赠，多捐不限，其意义在于最大限度地激发每个人的慈善之情，弘扬“不以善小而不为”的慈善理念，使每个人的爱心通过“慈善一元捐行动”得以实现。', 0, 1);
INSERT INTO `projects` VALUES ('f2c482a5-d217-11ef-b353-0250f2000002', 'https://pisiyi.oss-cn-beijing.aliyuncs.com/53f30ee362234840bd33b27f78ea013b.jpg', '慈爱孤儿工程', '20231008001', 120000.00, 4500.00, 2, '2023-10-07', '2025-12-31', '一张纸献爱心行动是总会于2008年发起的全国性慈善公益项目，旨在倡导机关、单位、学校、社区居民等捐赠废旧纸张及闲置物品或售卖后所得善款捐赠至总会，用于植树造林，绿化环境。倡导生态文明、推动绿色发展，培育全民节俭养德、资源节约、低碳生活。', 7, 1);
INSERT INTO `projects` VALUES ('f2c484b4-d217-11ef-b353-0250f2000002', 'https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20250107/0533f0dc87b649419064a704b7534acd.jpg', '心阳捐赠总会紧急驰援西藏地区抗震救灾', '20231008001', 120000.00, 4150.00, 4, '2024-12-27', '2025-12-31', '2025年1月7日9时5分，西藏日喀则市定日县发生6.8级地震，造成重大人员伤亡，大量房屋倒塌。此次募捐活动旨在广泛动员社会各界的力量，共同为受灾地区和困难群众提供援助。', 6, 1);
INSERT INTO `projects` VALUES ('f2c485ef-d217-11ef-b353-0250f2000002', 'https://res-img.n.gongyibao.cn/uploads/a01358c5-befc-4f81-9c85-48b42fa78b35/20240913/756099d666fb42b09caa8c7151c5a067.jpg', '中华慈善总会“翼梦同行”项目', '20231008001', 120000.00, 2220.00, 3, '2023-10-07', '2025-12-31', '项目以研学形式开展丰富多彩的社会活动，让困难家庭学生挖掘自身的闪光点，消除自卑心理，增强他们的自尊心和自信心，提升主动交往的意识，开拓视野，坚定信念，树立理想，助力健康快乐成长，促进青少年身心健康全面发展，共同守护祖国美好未来。', 3, 1);

-- ----------------------------
-- Table structure for public_info
-- ----------------------------
DROP TABLE IF EXISTS `public_info`;
CREATE TABLE `public_info`  (
  `id` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '公示信息唯一标识（UUID）',
  `type` tinyint(4) NOT NULL COMMENT '公示信息类型（1: 年会报告, 2: 审计报告, 3: 慈善年鉴, 4: 专享报告）',
  `file_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '文件URL',
  `image_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '图片URL',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_type`(`type` ASC) USING BTREE COMMENT '公示信息类型索引'
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '公示信息表' ROW_FORMAT = Dynamic;

